window.addEventListener("load",()=>{const e=document.getElementById("loader");e&&(e.classList.add("fade-out"),setTimeout(()=>e.remove(),500))});document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelectorAll(".animate"),t=new IntersectionObserver(e=>{e.forEach(e=>{e.isIntersecting&&(e.target.classList.add("visible"),t.unobserve(e.target))})},{threshold:.1});e.forEach(e=>t.observe(e))});